// Gelen ESP1 durum mesajını al
const status = msg.payload;

// Eğer payload boş veya donanım verisi yoksa, hiçbir şey yapma
if (!status || !status.hardware) { return null; }

// Her bir veri parçası için ayrı bir mesaj nesnesi oluştur
const msg_gameState = { payload: status.gameState };
const msg_levelDetail = { payload: status.levelDetail };
const msg_matrix = { payload: status.matrix };
const msg_timer = { payload: status.timer };
const msg_lastSound = { payload: status.hardware.lastSoundTrack };
const msg_dfPlayer = { payload: status.dfPlayerStatus ? "OK" : "Error" };
const msg_btn1 = { payload: status.hardware.button1_pressed };
const msg_btn2 = { payload: status.hardware.button2_pressed };
// YENİ EKLENENLER
const msg_btn3 = { payload: status.hardware.button3_pressed };
const msg_btn4 = { payload: status.hardware.button4_pressed };

// Sırasıyla tüm mesajları çıkışlara gönder
// Artık 10 çıkış var
return [
    msg_gameState,      // Çıkış 1
    msg_levelDetail,    // Çıkış 2
    msg_matrix,         // Çıkış 3
    msg_timer,          // Çıkış 4
    msg_lastSound,      // Çıkış 5
    msg_dfPlayer,       // Çıkış 6
